      method: 'get',
      port: '7480',
      type: 'xml', // xml -> json
      reqType: 'xml', // xml json -> xml
    }
  *
 */
commonApiConfig = (headers, api, data) => {
  if (isEnvDev && !isEnvMock) {
    return {
      url: `http://10.0.9.149:${api.port}${templateStrTransform(data, api.url)}`,
      data: paramsObjectParse(data, api.url),
