    this.taskType.uploading.splice(index, 1);
    this.taskType.break.push(storageObject);
    storageObject.completionTime = (new Date().toTimeString()).split(' ')[0];
    storageObject.state = 'break';
    storageObject.speed = '';
    storageObject.index = storageObject.total;

