/**
   * @param  {Function} fn         []
   * @param  {[Time]}   delayTime  [(ms)]
   * @param  {Boolean}  isImediate []
   * @param  {[type]}   args       []
  */
export function fnDebounce() {
  const fnObject = {};
  let timer;

  return (fn, delayTime, isImediate, args) => {
    // 
    const setTimer = () => {
      timer = setTimeout(() => {
        fn(args);
        // 
        clearTimeout(timer);
        delete (fnObject[fn]);
      }, delayTime);

      fnObject[fn] = {
        delayTime,
        timer,
      };
    };
    // 
    if (!delayTime || isImediate) return fn(args);
    // 
    if (fnObject[fn]) {
      clearTimeout(timer);
      setTimer(fn, delayTime, args);
    } else {
      setTimer(fn, delayTime, args);
    }
  };
}
